////////////////////////////////////////////
//功能：斜率计算
//输入：起点，终点，起点,终点所在数组
//输出：斜率
//备注：最小二乘法
///////////////////////////////////////////
double calculate_slope(int start, int end, int side[CAMERA_H]) {
    double ySum = 0;
    double y2Sum = 0;
    double xSum = 0;
    double xySum = 0;

    if (end >= NEAR_LINE) {
        end = NEAR_LINE - 1;
    }
    if (start <= 0) {
        start = 0;
    }
    int num = end - start + 1;
    for (int i = start; i <= end; i++) {
        xSum += side[i];
        ySum += i;
        y2Sum += i * i;
        xySum += i * side[i];
    }
    if ((num * y2Sum - ySum * ySum) != 0) {

        double Slope = (double)(num * xySum - xSum * ySum) / (num * y2Sum - ySum * ySum);
        return Slope;
    }
    else {
        return 0;
    }
}

