    int sumD = 0;
    for (int i = NEAR_LINE - 2; i >= 103; i--) {
        if (right_line[i] - left_line[i] >= 35) {
            sumD++;
        }
    }

    double kl = calculate_slope_uint(85, 100, left_line);
    double kr = calculate_slope_uint(85, 100, right_line);

    int sumU = 0;
    for (int i = 85; i <= 100; i++) {
        if (right_line[i] - left_line[i] < 35) {
            sumU++;
        }
    }

    if (sumD <= 7) {
        if (fabs(kl - kr) < 0.2) {
            if (sumU > 7) {
                state = stateStart;
            }
        }
    }

